import React from 'react';
import PropTypes from 'prop-types';
import { addSong, removeSong } from '../../services/favoriteSongsAPI';
import Loading from './Loading';

class MusicCard extends React.Component {
state = {
  loadingText: false,
  isFavorite: false,
}

componentDidMount() {
  this.markAsFav();
}

  // fun√ß√£o que checa se a m√∫sica √© marcada como favorita, para que essa informa√ß√£o seja mostrada mesmo depois de sair da pg
  markAsFav = () => {
    const { favList, trackId } = this.props;
    if (favList.some((song) => song.trackId === trackId)) {
      this.setState({ isFavorite: true });
    }
  }

  // fun√ß√£o para adicionar a musica aos favoritos
  addSongToFavorites = async () => {
    this.setState({ loadingText: true });
    await addSong(this.props);
    this.setState({ loadingText: false });
  }

   // fun√ß√£o para remover a musica dos favoritos
   removeSongFromFavorites = async () => {
     this.setState({ loadingText: true });
     await removeSong(this.props);
     this.setState({ loadingText: false });
   }

  handleCheckbox = ({ target: { checked } }) => {
    if (checked === true) {
      this.setState({ isFavorite: true });
      this.addSongToFavorites();
    } else {
      this.setState({ isFavorite: false });
      this.removeSongFromFavorites();
    }
  }

  render() {
    const { trackName, previewUrl, trackId } = this.props;
    const { loadingText, isFavorite } = this.state;
    if (loadingText) return <Loading />;
    return (
      <div className="songPreview">
        {/* <img alt="album cover" src={ artworkUrl100 } /> */}
        <h4>{trackName}</h4>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
          O seu navegador n√£o suporta o elemento
          {' '}
          <code>audio</code>
          .
        </audio>
        <div>
          <span className="saveFavs">ü§ç</span>
          {' '}
          <span>Save to your favorites: </span>
          <label htmlFor="favoriteInput">
            <input
              id="favoriteInput"
              data-testid={ `checkbox-music-${trackId}` }
              type="checkbox"
              onChange={ this.handleCheckbox }
              checked={ isFavorite }
            />
            Favorita
          </label>
        </div>
      </div>
    );
  }
}

MusicCard.propTypes = {
  trackName: PropTypes.string.isRequired,
  previewUrl: PropTypes.string.isRequired,
  trackId: PropTypes.string.isRequired,
  favList: PropTypes.shape({
    some: PropTypes.func.isRequired,
  }).isRequired,
};

export default MusicCard;
