import React from 'react';
import Header from './components/Header';
import Loading from './components/Loading';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import AlbumPreview from './components/AlbumPreview';

class Search extends React.Component {
  state = {
    searchField: '',
    searchedArtist: '',
    isSearchBtnDisabled: true,
    loadingText: true,
    returnedList: [],
  }

  // função que valida se o botão deve ser habilitado ou não (2 ou mais caracteres)
  validateSearchBtn = () => {
    const { searchField } = this.state;
    const min = 2;
    if (searchField.length >= min) {
      this.setState({ isSearchBtnDisabled: false });
    } else {
      this.setState({ isSearchBtnDisabled: true });
    }
  }

  // função para atualizar o estado a medida que o input de search muda.
  handleChange = ({ target: { value, name } }) => {
    this.setState({ [name]: value }, this.validateSearchBtn);
  }

// função onclick para fazer a requisição e limpar input
onClickSearchBtn = async () => {
  const { searchField } = this.state;
  this.setState({ loadingText: false });
  const returnAPI = await searchAlbumsAPI(searchField);
  console.log(returnAPI); // array de objetos
  this.setState({ searchedArtist: returnAPI[0].artistName,
    returnedList: returnAPI,
    searchField: '',
    loadingText: true });
}
// função que verifica se array retornado da api é vazio 
isReturnEmpty
render() {
  const { isSearchBtnDisabled, loadingText, searchedArtist, returnedList } = this.state;
  return (
    <div data-testid="page-search">
      <Header />
      {
        loadingText ? (
          <form>
            <label htmlFor="searchField">
              Search
              <input
                name="searchField"
                onChange={ this.handleChange }
                id="searchField"
                type="text"
                data-testid="search-artist-input"
              />
            </label>
            <button
              disabled={ isSearchBtnDisabled }
              onClick={ this.onClickSearchBtn }
              type="button"
              data-testid="search-artist-button"
            >
              Pesquisar
            </button>
          </form>
        ) : <Loading />
      }
      <h4>
        Resultado de álbuns de:
        {' '}
        { searchedArtist }
      </h4>
      <ol>
        {
          returnedList.flatMap((obj) => (
            <li>
              <AlbumPreview { ... obj } />
            </li>
          ))
        }
      </ol>
    </div>
  );
}
}

export default Search;
