import React from 'react';
import { getUser } from '../../services/userAPI';
import Loading from './Loading';

class Header extends React.Component {
state ={
  userName: '',
  loadingText: false,
}
componentDidMount() {
  getUserInfo();
}

// função para pegar valores da getUser e atualizar o estado
getUserInfo = async () => {
  this.setState({ loadingText: true });
  const userData = await getUser();
  this.setState({ userName: userData.name, loadingText: false });
}

render() {
  const { userName, loadingText } = this.state;
  if (loadingText) return <Loading />;
  return (
    <header data-testid="header-component">
      <section data-testid="header-user-name">
        { `Welcome, ${userName}` }
      </section>
    </header>
  );
}
}

export default Header;
