     userName: '',
     isButtonDisabled: true,
   }

   render() {
     return (
       <div data-testid="page-login">
         <h3> Login Form</h3>
         <form>
           <label htmlFor="nameLogin">
             Name
             <input
               onChange={ handleChange }
               id="nameLogin"
               name="userName"
               data-testid="login-name-input"
               type="text"
               placeholder="Username"
             />
           </label>
           <button
             data-testid="login-submit-button"
             type="button"
           >
             Entrar
           </button>
         </form>
       </div>
     );
   }
}

export default Login;
