import React from 'react';
import Header from './components/Header';

class Search extends React.Component {
  state = {
    searchField: '',
    isSearchBtnDisabled: true,
  }

  // função que valida se o botão deve ser habilitado ou não (2 ou mais caracteres)
  validateEnterBtn = () => {
    const { searchField } = this.state;
    const min = 2;
    if (userName.length >= min) {
      this.setState({ isButtonDisabled: false });
    } else {
      this.setState({ isButtonDisabled: true });
    }
  }

  // função para atualizar o estado a medida que o input muda.
  handleChange = ({ target: { value, name } }) => {
    this.setState({ [name]: value }, this.validateEnterBtn);
  }

  // função para onCLick >> chama função createUser
  handleClick = async () => {
    const { userName } = this.state;
    const { history } = this.props;
    this.setState({ loadingText: true });
    await createUser({ name: userName });
    this.setState({ loadingText: false });
    history.push('/search');
  }

  render() {
    return (
      <div data-testid="page-search">
        <Header />
        <form>
          <label htmlFor="searchField">
            <input
              name="searchField"
              onChange={ this.handleChange }
              id="searchField"
              type="text"
              data-testid="search-artist-input"
            />
          </label>
          <button
            onClick={ this.handleClick }
            type="button"
            data-testid="search-artist-button"
          >
            Pesquisar
          </button>
        </form>
      </div>
    );
  }
}

export default Search;
