import React from 'react';
import Header from './components/Header';

class Search extends React.Component {
  state = {
    searchField: '',
    isSearchBtnDisabled: true,
    loadingText: false,
  }

  // função que valida se o botão deve ser habilitado ou não (2 ou mais caracteres)
  validateSearchBtn = () => {
    const { searchField } = this.state;
    const min = 2;
    if (searchField.length >= min) {
      this.setState({ isSearchBtnDisabled: false });
    } else {
      this.setState({ isSearchBtnDisabled: true });
    }
  }

  // função para atualizar o estado a medida que o input de search muda.
  handleChange = ({ target: { value, name } }) => {
    this.setState({ [name]: value }, this.validateSearchBtn);
  }

// função onclick para fazer a requisição e limpar input
onClickSearchBtn = async () => {
  const { searchField, loadingText } = this.state;
  this.setState({ loadingText: true });
  await searchAlbumsAPI(searchField);
  this.setState({ searchField: '', loadingText: true });
}

render() {
  const { isSearchBtnDisabled } = this.state;
  return (
    <div data-testid="page-search">
      <Header />
      <form>
        <label htmlFor="searchField">
          Search
          <input
            name="searchField"
            onChange={ this.handleChange }
            id="searchField"
            type="text"
            data-testid="search-artist-input"
          />
        </label>
        <button
          disabled={ isSearchBtnDisabled }
          type="button"
          data-testid="search-artist-button"
        >
          Pesquisar
        </button>
      </form>
    </div>
  );
}
}

export default Search;
