import React from 'react';
import { Link } from 'react-router-dom';
import { getUser } from '../../services/userAPI';
import Loading from './Loading';

class Header extends React.Component {
state ={
  userName: '',
  loadingText: false,
}

componentDidMount() {
  this.getUserInfo();
}

// função para pegar valores da getUser e atualizar o estado
getUserInfo = async () => {
  this.setState({ loadingText: true });
  const userData = await getUser();
  this.setState({ userName: userData.name, loadingText: false });
}

render() {
  const { userName, loadingText } = this.state;
  if (loadingText) return <Loading />;
  return (
    <header className="header" data-testid="header-component">
      <img alt="calvitoria Sound" src="https://user-images.githubusercontent.com/95686401/169414429-b4f330f7-118f-4752-b09b-48c917819302.png" />
      <img className="" alt="calvitoria Sound" src="https://user-images.githubusercontent.com/95686401/169417079-aaa685dc-a475-45e4-af01-2e1e0579cb54.png" />
      <div className="containerRightSide">
        <section className="nameContainer" data-testid="header-user-name">
          <h3>{ `Welcome, ${userName}` }</h3>
        </section>
        <nav>
          <Link className="a" data-testid="link-to-search" to="/search">Search</Link>
          <Link className="a" data-testid="link-to-favorites" to="/favorites">
            Favorites
          </Link>
          <Link className="a" data-testid="link-to-profile" to="/profile">Profile</Link>
        </nav>
      </div>
    </header>
  );
}
}

export default Header;
